import React, { useState, useEffect } from 'react';
import caasy from '@caasy/sdk-js';

import Main from '../components/layout/main/Main';
import PostPreview from '../components/postPreview/PostPreview';
import PageControls from '../components/pageControls/PageControls';

import blogConfig from '../blogConfig';
import utils from '../utils/';
import {getAllPostIds, getPostData} from '../utils/getPosts'
import API_URL from "../utils/http-utils";

import ReactPaginate from 'react-paginate';
import Router, { withRouter } from 'next/router'

/**
 * Displays all blog posts for a given page number.
 * @param posts {array} all blog posts for the given page
 * @param pageControls {array} contains the pages for the navigation
 * @param currentPage {number} number of the active page
 * @returns {*}
 * @constructor
 */
const Posts = ({ posts, pageControls, currentPage }) => {

    const [isLoading, setLoading] = useState(false); //State for the loading indicator
    const startLoading = () => setLoading(true);
    const stopLoading = () => setLoading(false);

    useEffect(() => { //After the component is mounted set router event handlers
        Router.events.on('routeChangeStart', startLoading);
        Router.events.on('routeChangeComplete', stopLoading);

        return () => {
            Router.events.off('routeChangeStart', startLoading);
            Router.events.off('routeChangeComplete', stopLoading);
        }
    }, [])

    const pagginationHandler = (page) => {
        const currentPath = props.router.pathname;
        const currentQuery = props.router.query;
        currentQuery.page = page.selected + 1;

        props.router.push({
            pathname: currentPath,
            query: currentQuery,
        });

    };

    let content = null;
    if (isLoading)
        content = <div>Loading...</div>;
    else {
        //Generating posts list
        content = (
            <Main>
                {(posts || []).map(post => (
                    <PostPreview
                        key={post.id}
                        id={post.id}
                        title={post.title}
                        previewText={post.text}
                        previewImage={post.image}
                        lastEdit={new Date(post.created_at).toDateString()}
                    />
                ))}

                <PageControls data={pageControls} active={currentPage}/>
            </Main>
        );
    }

    return (
        <div className="container">
            <h1>Posts List with Pagination in Next.js</h1>
            <div className="posts">
                {content}
            </div>

            <ReactPaginate
                previousLabel={'previous'}
                nextLabel={'next'}
                breakLabel={'...'}
                breakClassName={'break-me'}
                activeClassName={'active'}
                containerClassName={'pagination'}
                subContainerClassName={'pages pagination'}

                initialPage={props.currentPage - 1}
                pageCount={props.pageCount}
                marginPagesDisplayed={2}
                pageRangeDisplayed={5}
                onPageChange={pagginationHandler}
            />
        </div>
    );
};


//Fetching posts in get Intial Props to make the app seo friendly
Posts.getInitialProps = async ({ query }) => {
    const page = query.page || 1; //if page empty we request the first page
    const res = await fetch(API_URL);
    const posts = await res.json()
    return {
        totalCount: 5,
        pageCount: 2,
        currentPage: 1,
        perPage: 2,
        posts: posts,
    };
}


export default withRouter(Posts);

