import React from 'react';
import {getAllPostIds, getPostData} from '../../utils/getPosts'


import BlogPost from '../../components/blogPost/BlogPost';


/**
 * Renders the blog post for the given data.
 * @param post
 * @returns {*}
 * @constructor
 */
const Post = (post) => <BlogPost post={post.postData} />;

export default Post;



export async function getStaticPaths({ locales }) {
    const paths = await getAllPostIds()
    console.log(paths)
    return {
        paths: [
            { params: { slug: paths.params.slug }, locale: 'en-US' },
            { params: { slug: paths.params.slug  }, locale: 'ru' },
        ],
        fallback: false
    }
}

export async function getStaticProps({ params, locale }) {
    const postData = await getPostData(params.slug)
    return {
        props: {
            postData
        }
    }
}
